local self = {}LegacyCompat.IFileSystem = LegacyCompat.MakeConstructor (self)function self:ctor ()	self.BaseLuaPath        = "lua/"	self.BasePath           = ""	self.VirtualBaseLuaPath = nil	self.VirtualBasePath    = nilendfunction self:Exists (path, lua)	LegacyCompat.Error ("IFileSystem:Exists : Not implemented")endfunction self:Find (path, lua)	LegacyCompat.Error ("IFileSystem:Find : Not implemented")endfunction self:GetBaseLuaPath ()	return self.BaseLuaPathendfunction self:GetBasePath ()	return self.BasePathendfunction self:GetVirtualBaseLuaPath ()	return self.VirtualBaseLuaPath or self:GetBaseLuaPath ()endfunction self:GetVirtualBasePath ()	return self.VirtualBasePath or self:GetBasePath ()endfunction self:Read (path, lua)	LegacyCompat.Error ("IFileSystem:Read : Not implemented")endfunction self:SetBaseLuaPath (path)	self.BaseLuaPath = pathendfunction self:SetBasePath (path)	self.BasePath = pathendfunction self:SetVirtualBaseLuaPath (path)	self.VirtualBaseLuaPath = pathendfunction self:SetVirtualBasePath (path)	self.VirtualBasePath = pathendfunction self:ToString ()	return "[IFileSystem: " .. self:GetBaseLuaPath () .. " -> " .. self:GetVirtualBaseLuaPath () .. "]"end